console.log('positional audio started');

var exports = module.exports = {}

exports.startAudio = function () {
    const { Howl, Howler } = require('howler');
    var sound = new Howl({
        src: ['./node_modules/positional-audio/media/testtrack.wav'],
        autoplay: true,
        loop: true,
        volume: 0.5,
        onplay: function () {
            this.pos(2, 3, 4);
        },
        onend: function () {
            console.log('Finished!');
        }
    });

    console.log('audio started');
}